import React from "react";

import { DataGrid } from "@mui/x-data-grid";

import {
	ListaUsuariosContainer,
	BotaoEditar,
	BotaoApagar,
} from "./ListaUsuariosElementos";

import { Link } from "react-router-dom";

const columns = [
	{ field: "id", headerName: "ID", width: 70 },
	{
		field: "usuario",
		headerName: "Usuário",
		width: 180,
		renderCell: (params) => {
			return (
				<div style={{ display: "flex", alignItems: "center" }}>
					<img
						style={{
							height: "32px",
							width: "32px",
							borderRadius: "50%",
							marginRight: "10px",
						}}
						src={params.row.avatar}
						alt=""
					/>
					{params.row.nome_usuario}
				</div>
			);
		},
	},
	{
		field: "email",
		headerName: "E-mail",
		type: "email",
		width: 180,
	},
	{
		field: "status",
		headerName: "Status",
		width: 120,
	},
	{
		field: "acao",
		headerName: "Ação",
		width: 180,
		renderCell: (params) => {
			return (
				<>
					<Link to={"/usuario/" + params.row.id}>
						<BotaoEditar>Editar</BotaoEditar>
					</Link>
					<BotaoApagar />
				</>
			);
		},
	},
];

const rows = [
	{
		id: 1,
		nome_usuario: "ramonramon",
		avatar: "https://avatarfiles.alphacoders.com/165/thumb-165945.jpg",
		email: "ramon@gmail.com",
		status: 0,
	},
	{
		id: 2,
		nome_usuario: "ramonramon",
		avatar: "https://avatarfiles.alphacoders.com/165/thumb-165945.jpg",
		email: "ramon@gmail.com",
		status: 0,
	},
	{
		id: 3,
		nome_usuario: "ramonramon",
		avatar: "https://avatarfiles.alphacoders.com/165/thumb-165945.jpg",
		email: "ramon@gmail.com",
		status: 0,
	},
	{
		id: 4,
		nome_usuario: "ramonramon",
		avatar: "https://avatarfiles.alphacoders.com/165/thumb-165945.jpg",
		email: "ramon@gmail.com",
		status: 0,
	},
	{
		id: 5,
		nome_usuario: "ramonramon",
		avatar: "https://avatarfiles.alphacoders.com/165/thumb-165945.jpg",
		email: "ramon@gmail.com",
		status: 0,
	},
	{
		id: 6,
		nome_usuario: "ramonramon",
		avatar: "https://avatarfiles.alphacoders.com/165/thumb-165945.jpg",
		email: "ramon@gmail.com",
		status: 0,
	},
	{
		id: 7,
		nome_usuario: "ramonramon",
		avatar: "https://avatarfiles.alphacoders.com/165/thumb-165945.jpg",
		email: "ramon@gmail.com",
		status: 0,
	},
	{
		id: 8,
		nome_usuario: "ramonramon",
		avatar: "https://avatarfiles.alphacoders.com/165/thumb-165945.jpg",
		email: "ramon@gmail.com",
		status: 0,
	},
];

const ListaUsuarios = () => {
	return (
		<ListaUsuariosContainer>
			<div
				style={{
					width: "80%",
					height: "80%",
					margin: "0 auto",
					marginTop: "50px",
				}}
			>
				<DataGrid
					rows={rows}
					columns={columns}
					pageSize={10}
					rowsPerPageOptions={[5]}
					checkboxSelection
					disableSelectionOnClick
				/>
			</div>
		</ListaUsuariosContainer>
	);
};

export default ListaUsuarios;
