import styled from "styled-components";

export const Container = styled.div`
	flex: 4;
`;

export const Formulario = styled.form`
	display: flex;
	flex-wrap: wrap;
`;

export const Campo = styled.div`
	width: 400px;
	display: flex;
	flex-direction: column;
	margin-top: 10px;
	margin-right: 20px;

	label {
		margin-bottom: 10px;
		font-size: 14px;
		font-weight: 600;
		color: rgb(150, 150, 150);
	}

	input {
		height: 20px;
		padding: 10px;
		border: 1px solid grey;
		border-radius: 5px;
	}
`;
